
function mockRomeClient (conf) {
    return function (action, data, callback) {
        for (var i = 0; i < conf.length; i++) {
            if (action == conf[i].action) {
                if ('res' in conf[i]) {
                    callback(null, conf[i].res);
                }
                else {
                    callback(conf[i].err, null);
                };
                break;
            };
        };
    }
}

module.exports = mockRomeClient;
